import React, { useEffect, useState } from "react";

import config from "../../config/config.json";

import * as auth from "../../../services/auth";
import * as base from "../../../methods/data";

import LoginForm from "./forms/loginForm";

const Login = (props) => {
    const { user, title, location } = props;
    const [posts, setPosts] = useState(null);
    const [sell, setSell] = useState(null);
    const [authorData, setAuthorData] = useState(null);

    function redirect() {
        window.location = location.state
            ? location.state.from.pathname
            : "/login";
    }

    useEffect(() => {
        const result = data.filter(
            (card) => card.author && card.author._id == user._id
        );
        setauthorData(result);
    }, [data]);

    useEffect(() => {
        title(user ? config.headers.user : config.headers.login);
    }, []);

    return (
        <div className="screen">
            {data && <div></div>}

            <div className="login">
                {!data && user && (
                    <>
                        <div className="login__info">
                            <div>{config.info.loggedAs}</div>
                            <div>{user.login}</div>
                        </div>

                        <div>
                            <button
                                className="actionBtn"
                                onClick={async () =>
                                    setData(await base.getProducts())
                                }
                            >
                                {config.user.sell}
                            </button>

                            <button
                                className="actionBtn"
                                onClick={async () =>
                                    setData(await base.getNotices())
                                }
                            >
                                {config.user.posts}
                            </button>
                        </div>

                        <button
                            className="actionBtn"
                            onClick={() => auth.logout()}
                        >
                            {config.actions.logout}
                        </button>
                    </>
                )}

                {!data && !user && <LoginForm redirect={redirect} />}
            </div>
        </div>
    );
};

export default Login;
