const getOne = async (id, base, setEdit, setImage) => {
    if (id) {
        const response = await base.getOne(id);
        if (!response.ok) return false;
        setEdit(response.data);
        if (setImage) setImage(response.data.images);
    }
    return true;
};

export { getOne };
