import React from "react";
import { Route, Redirect } from "react-router-dom";
import * as auth from "../../../api/auth";

const ProtectedRoute = (props) => ({
    path,
    component: Component,
    render,

    ...rest
}) => {
    return (
        <Route
            {...rest}
            render={(props) => {
                if (!auth.getUser)
                    return (
                        <Redirect
                            to={{
                                pathname: "/login",
                                state: { from: props.location },
                            }}
                        />
                    );
                return Component ? (
                    <Component {...props} rest />
                ) : (
                    render(props)
                );
            }}
        />
    );
};

export default ProtectedRoute;
