import React, { useState, useEffect } from "react";

import * as catBase from "../../api/categories";
import * as prodBase from "../../api/products";
import * as imagesBase from "../../api/images";

import { Formik } from "formik";
import * as Yup from "yup";

import AddTextBox from "./common/form/addTextBox";
import AddInputBox from "./common/form/addInputBox";
import AddSelectBox from "./common/form/addSelectBox";
import AddSubmitButton from "./common/form/submitButton";
import MultiImageInput from "react-multiple-image-input";

import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const validationSchema = Yup.object().shape({
    title: Yup.string()
        .required("Nazwa jest wymagana.")
        .min(3)
        .typeError("Nieprawidłowa wartość.")
        .max(120)
        .label("Nazwa"),
    text: Yup.string().max(6000),
    price: Yup.number()
        .typeError("Nieprawidłowa wartość.")
        .max(9000000)
        .test("is-decimal", "invalid decimal", (value) =>
            (value + "").match(/d*/)
        ),
    category: Yup.string().required().max(255),
});

const AddProduct = (props) => {
    const [image, setImage] = useState([]);
    const [categories, setCategories] = useState(null);
    const [edit, setEdit] = useState(null);

    const checkEdit = async () => {
        if (props.match.params.id) {
            const response = await prodBase.product(props.match.params.id);
            if (!response.ok) return;
            setEdit(response.data);
        }

        // edit.image ? setImage(edit.image) : null;
    };

    useEffect(() => {
        console.log(image);
    }, [image]);

    useEffect(() => {
        props.title("sprzedaj");
        loadData();
        checkEdit();
    }, []);

    const loadData = async () => {
        const result = await catBase.get();

        if (!Array.isArray(result.data)) return;
        setCategories(result.data);
    };

    function toFixedIfNecessary(value, dp) {
        return +parseFloat(value).toFixed(dp);
    }

    const handleSave = async (item, image) => {
        uploadImage(image[0]);

        if (item.price) {
            item.price = parseFloat(item.price.trim().replace(" ", ""));
            item.price = toFixedIfNecessary(item.price, 2);
            item.price = item.price.toString();
        }

        if (edit) {
            item._id = edit._id;
            const response = await prodBase.edit(item);
            response.ok
                ? props.history.push(`/bazar/product/${response.data._id}`)
                : toast.error("Error occured on saving!");

            return;
        }

        const response = await prodBase.add(item);
        response.ok
            ? props.history.push(`/bazar/product/${response.data._id}`)
            : toast.error("Error occured on saving!");
    };

    const uploadImage = async (pathToImageOnFilesystem) => {
        // const form = pathToImageOnFilesystem;

        // const form = document.getElementById("file").value;

        const form = image;
        // console.log(form);
        const response = await imagesBase.add(form);

        console.log(response);
        return;
    };

    return (
        <div className="screen">
            {categories && (
                <div className="screen__container">
                    <div className="addBoard">
                        <ToastContainer />
                        <div className="multipleImages"></div>
                        <div className="multipleImages">
                            <MultiImageInput
                                allowCrop={false}
                                max={6}
                                images={image}
                                setImages={setImage}
                                theme={{
                                    margin: "0",
                                    background: "unset",
                                    outlineColor: "#262a33",
                                    textColor: "rgba(255,255,255,0.6)",
                                    buttonColor: "#ff0e1f",
                                    modalColor: "#ffffff",
                                }}
                            />
                        </div>
                        <input
                            type="file"
                            id="file"
                            name="file"
                            onChange={(e) => setImage(e.target.files[0])}
                        />
                        <Formik
                            enableReinitialize={true}
                            initialValues={
                                edit
                                    ? {
                                          text: edit.text,
                                          title: edit.title,
                                          price: edit.price,
                                          category: edit.category._id,
                                      }
                                    : {
                                          text: "",
                                          title: "",
                                          price: "",
                                          category: categories[0]._id,
                                      }
                            }
                            onSubmit={(values) => handleSave(values, image)}
                            validationSchema={validationSchema}
                        >
                            {({}) => (
                                <>
                                    <AddInputBox
                                        className="addBoard__title"
                                        name="title"
                                        placeholder="Nazwa"
                                        defaultValue={edit ? edit.title : null}
                                    />

                                    <AddTextBox
                                        className="addBoard__text"
                                        name="text"
                                        placeholder="Opis..."
                                        defaultValue={edit ? edit.text : null}
                                    />

                                    <div className="addBoard__details">
                                        <div>
                                            <AddInputBox
                                                label="CENA"
                                                placeholder="cena"
                                                name="price"
                                                id="price"
                                                className="addBoard__details--price"
                                                defaultValue={
                                                    edit ? edit.price : null
                                                }
                                            />
                                        </div>

                                        <div>
                                            {categories && (
                                                <AddSelectBox
                                                    label="KATEGORIA"
                                                    arr={categories}
                                                    id="category"
                                                    name="category"
                                                    className="addBoard__details--category"
                                                    defaultValue={
                                                        edit
                                                            ? edit.category._id
                                                            : null
                                                    }
                                                />
                                            )}
                                        </div>
                                    </div>

                                    <div className="addBoard__buttons">
                                        <AddSubmitButton
                                            className="actionBtn"
                                            title={
                                                edit ? "ZAPISZ" : "OPUBLIKUJ"
                                            }
                                        />
                                    </div>
                                </>
                            )}
                        </Formik>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AddProduct;
