import React, { useEffect } from "react";
import { NavLink } from "react-router-dom";
import { Formik } from "formik";

import config from "../../config/config.json";

import * as auth from "../../../services/auth";

import AddInputBox from "../common/form/addInputBox.jsx";
import AddSubmitButton from "../common/form/submitButton.jsx";
import Buttons from "../common/buttons.jsx";

const Login = (props) => {
    const { user, title, location } = props;

    useEffect(() => {
        title(config.headers.user);
    }, []);

    return (
        <div className="screen">
            <div className="login">
                {user && (
                    <>
                        <div className="login__info">
                            <div>{config.info.loggedAs}</div>
                            <div>{user.login}</div>
                        </div>

                        <button
                            className="actionBtn"
                            onClick={() => auth.logout()}
                        >
                            {config.actions.logout}
                        </button>
                    </>
                )}

                {!user && (
                    <Formik
                        initialValues={{
                            email: "",
                            password: "",
                        }}
                        onSubmit={(values) => handleLogin(values)}
                        validationSchema={validationSchema}
                    >
                        {({}) => (
                            <>
                                <div className="login__buttons">
                                    <NavLink to="/register">
                                        <RiUserAddLine className="specialBtn" />
                                    </NavLink>

                                    <Buttons
                                        buttons={[
                                            {
                                                name: <RiUserAddLine />,
                                                ref: "/register",
                                            },
                                        ]}
                                    />

                                    <AddInputBox
                                        // className="addBoard__title"
                                        name="email"
                                        label={config.actions.email}
                                        placeholder="Email"
                                    />

                                    <AddInputBox
                                        // className="addBoard__title"
                                        label={config.actions.password}
                                        name="password"
                                        type="password"
                                        placeholder="Password"
                                    />
                                </div>

                                <AddSubmitButton
                                    className="actionBtn"
                                    title={config.actions.login}
                                />
                            </>
                        )}
                    </Formik>
                )}
            </div>
        </div>
    );
};

export default Login;
