import React, { useEffect, useState } from "react";

import config from "../../config/config.json";

import * as auth from "../../../services/auth";
import * as base from "../../../methods/data";

import LoginForm from "./forms/loginForm";

const Login = (props) => {
    const { user, title, location } = props;
    const [data, setData] = useState(null);

    function redirect() {
        console.log("fasdfa");
        window.location = location.state
            ? location.state.from.pathname
            : "/login";
    }

    useEffect(() => {
        title(user ? config.headers.user : config.headers.login);
    }, []);

    return (
        <div className="screen">
            <div className="login">
                {data && data.filter(card.author._id == user._id)}
                {user && (
                    <>
                        <div className="login__info">
                            <div>{config.info.loggedAs}</div>
                            <div>{user.login}</div>
                        </div>

                        <div>
                            <button
                                className="actionBtn"
                                onClick={() => setData(base.getProducts())}
                            >
                                {config.user.sell}
                            </button>

                            <button
                                className="actionBtn"
                                onClick={() => setData(base.getNotices())}
                            >
                                {config.user.posts}
                            </button>
                        </div>

                        <button
                            className="actionBtn"
                            onClick={() => auth.logout()}
                        >
                            {config.actions.logout}
                        </button>
                    </>
                )}

                {!user && <LoginForm redirect={redirect} />}
            </div>
        </div>
    );
};

export default Login;
