import React, { useEffect, useState } from "react";

import ImageSlider from "./common/imageSlider";
import AcceptModal from "./common/modal";

import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import * as prodBase from "../../api/products";
import * as imagesBase from "../../api/images";

import * as auth from "../../api/auth";

function ProductDetails(props) {
    const [card, setCard] = useState(null);
    const [modal, setModal] = useState(false);

    useEffect(() => {
        loadData();
    }, []);

    useEffect(() => {
        if (card) props.title(card.title);
        console.log(card);
    }, [card]);

    const loadData = async () => {
        console.log(card);
        const product = await prodBase.product(props.match.params.id);
        setCard(product.data);
    };

    const handleDelete = async (card) => {
        try {
            card.images.forEach((img) => imagesBase.remove(img));
            const result = await prodBase.remove(card._id);
            if (result.ok) return props.history.push("/bazar");
            toast.error("Error when delete. " + result.originalError.message);
        } catch (ex) {
            if (ex.response && ex.response.status === 404)
                toast.error("This product has already been deleted.");
            else toast.error("Error when delete.");
        }
    };

    const user = auth.getUser();

    return (
        <div className="screen">
            <ToastContainer />
            {card && (
                <div className="screen__container">
                    <div className="productDetails__image">
                        <ImageSlider
                            images={card.images}
                            handleClick={(i, index, arr) =>
                                props.renderImage(arr, index)
                            }
                        />
                    </div>

                    <div className="productDetails__details">
                        <p>
                            {card.author.name
                                ? card.author.name
                                : card.author.login}
                        </p>
                        <p>{card.date}</p>
                    </div>
                    {card.price !== 0 && (
                        <div className="productDetails__image--price">
                            <span>{card.price} zł</span>
                        </div>
                    )}
                    <div className="productDetails__text">
                        <p> {card.text}</p>
                    </div>

                    {modal && (
                        <AcceptModal
                            close={setModal}
                            accept={() => handleDelete(card)}
                            title={`Potwierdź operację usunięcia ${card.title}`}
                        />
                    )}

                    {user && user._id === card.author._id && !modal && (
                        <div className="productDetails__buttons">
                            <button
                                className="actionBtn edit"
                                onClick={() =>
                                    props.history.push(`edit/${card._id}`)
                                }
                            >
                                EDYTUJ
                            </button>
                            <button
                                className="actionBtn delete"
                                onClick={() => setModal(true)}
                            >
                                USUŃ
                            </button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default ProductDetails;
