import { apiClient as client } from "./client";

const endpoint = "/notices";

const get = async () => client.get(endpoint);

const getOne = async (id) => client.get(`${endpoint}/${id}`);

const add = async (body) => client.post(endpoint, body);

const edit = async (id, body) => client.put(`${endpoint}/${id}`, body);

const remove = async (body) => client.delete(endpoint + "/" + body);

export { get, add, edit, remove, getOne };
