import React, { useEffect, useState } from "react";

import ImageSlider from "../common/imageSlider";
import AcceptModal from "../common/modal";

import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import * as base from "../../../methods/data";

import * as auth from "../../../api/auth";
import { NavLink } from "react-router-dom";

function ProductDetails(props) {
    const [card, setCard] = useState(null);
    const [modal, setModal] = useState(false);

    useEffect(async () => {
        if (!(await base.getOneProduct(props.match.params.id, setCard, null)))
            props.history.push("/bazar");
    }, []);

    useEffect(() => {
        if (card) props.title(card.title);
        console.log(card);
    }, [card]);

    const handleDelete = async (card) => {
        base.productDelete();
    };

    const user = auth.getUser();

    return (
        <div className="screen">
            <NavLink
                to={{
                    pathname: "/bazar",
                    state: props.history.location.state,
                }}
            >
                <button style={{ zIndex: 7 }}>asfsf</button>
            </NavLink>

            <ToastContainer />
            {card && (
                <div className="screen__container">
                    <div className="productDetails__image">
                        <ImageSlider
                            images={card.images}
                            handleClick={(i, index, arr) =>
                                props.renderImage(arr, index)
                            }
                        />
                    </div>

                    <div className="productDetails__details">
                        <p>
                            {card.author.name
                                ? card.author.name
                                : card.author.login}
                        </p>
                        <p>{card.date}</p>
                    </div>
                    {card.price !== 0 && (
                        <div className="productDetails__image--price">
                            <span>{card.price} zł</span>
                        </div>
                    )}
                    <div className="productDetails__text">
                        <p> {card.text}</p>
                    </div>

                    {modal && (
                        <AcceptModal
                            close={setModal}
                            accept={() => handleDelete(card)}
                            title={`Potwierdź operację usunięcia ${card.title}`}
                        />
                    )}

                    {user && user._id === card.author._id && !modal && (
                        <div className="productDetails__buttons">
                            <button
                                className="actionBtn edit"
                                onClick={() =>
                                    props.history.push(`edit/${card._id}`)
                                }
                            >
                                EDYTUJ
                            </button>
                            <button
                                className="actionBtn delete"
                                onClick={() => setModal(true)}
                            >
                                USUŃ
                            </button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default ProductDetails;
