import { create } from "apisauce";

const apiClient = create({
    baseURL: "http://192.168.56.1:5000/",
});

setToken(localStorage.getItem("token"));

const setToken = (token) => {
    apiClient.setHeader("x-auth-token", token);
};

export { apiClient, setToken };
