import ImageSlider from "../common/imageSlider";
import AcceptModal from "../common/modal";
import * as time from "../../../methods/time";

const Details = ({ card, modal, handleDelete, props }) => {
    const { user, renderImage, title, history, match } = props;

    return (
        <>
            {card && (
                <div className="screen__container">
                    {card.images.length > 0 && (
                        <div className="productDetails__image">
                            <ImageSlider
                                images={card.images}
                                handleClick={(i, index, arr) =>
                                    renderImage(arr, index)
                                }
                            />
                        </div>
                    )}
                    <div className="productDetails__details">
                        {card.author && (
                            <p>
                                {card.author.login
                                    ? card.author.login
                                    : "Undefined"}
                            </p>
                        )}
                        <p>{time.getDate(card.date)}</p>
                    </div>
                    {card.price !== 0 && (
                        <div className="productDetails__image--price">
                            <span>{card.price} zł</span>
                        </div>
                    )}
                    <div className="productDetails__text">
                        <p> {card.text}</p>
                    </div>
                    <div className="">
                        {card.author && card.author.phone && (
                            <a href={`tel:${card.author.phone}`}>
                                CLICK TO CALL
                            </a>
                        )}
                    </div>

                    {modal && (
                        <AcceptModal
                            close={setModal}
                            accept={() => handleDelete(card)}
                            title={`Potwierdź operację usunięcia ${card.title}`}
                        />
                    )}
                    {card.author &&
                        user &&
                        user._id === card.author._id &&
                        !modal && (
                            <div className="productDetails__buttons">
                                <button
                                    className="actionBtn edit"
                                    onClick={() =>
                                        history.push(`edit/${card._id}`)
                                    }
                                >
                                    EDYTUJ
                                </button>
                                <button
                                    className="actionBtn delete"
                                    onClick={() => setModal(true)}
                                >
                                    USUŃ
                                </button>
                            </div>
                        )}
                </div>
            )}
        </>
    );
};

export default Details;
