import React, { useEffect } from "react";

import { NavLink } from "react-router-dom";

import { Redirect, Route, Switch } from "react-router";

import { RiUserAddLine } from "react-icons/ri";

import { Formik } from "formik";
import * as Yup from "yup";

import { apiClient } from "../../api/client";

import { pick } from "lodash";

import * as auth from "../../api/auth";

import AddInputBox from "./common/form/addInputBox.jsx";
import AddSubmitButton from "./common/form/submitButton.jsx";
import Buttons from "./common/buttons.jsx";

const validationSchema = Yup.object().shape({
    email: Yup.string().trim().required("Podaj email.").label("Email").email(),
    password: Yup.string().min(5).max(6000),
});

const Login = (props) => {
    useEffect(() => {
        props.title(props.user ? props.user : "login");
    }, []);

    const handleLogin = async (user) => {
        user = pick(user, ["email", "password"]);
        const response = await auth.login(user);

        if (response.ok) {
            localStorage.setItem("token", response.headers["x-auth-token"]);
            apiClient.setHeader(
                "x-auth-token",
                response.headers["x-auth-token"]
            );
            props.setUser("user");
            console.log("props ", props);
            // props.history.push("/");
        }
    };

    return (
        <div className="screen">
            <div className="login">
                {props.user && (
                    <>
                        <div className="login__info">
                            <div>Zalogowany jako:</div>
                            <div>{props.user}</div>
                        </div>

                        <button
                            className="actionBtn"
                            onClick={() => auth.logout()}
                        >
                            WYLOGUJ
                        </button>
                    </>
                )}

                {!props.user && (
                    <Formik
                        initialValues={{
                            email: "",
                            password: "",
                        }}
                        onSubmit={(values) => handleLogin(values)}
                        validationSchema={validationSchema}
                    >
                        {({}) => (
                            <>
                                <div className="login__buttons">
                                    <NavLink to="/register">
                                        <RiUserAddLine className="specialBtn" />
                                    </NavLink>

                                    <Buttons
                                        buttons={[
                                            {
                                                name: <RiUserAddLine />,
                                                ref: "/register",
                                            },
                                        ]}
                                    />

                                    <AddInputBox
                                        // className="addBoard__title"
                                        name="email"
                                        label="Email"
                                        placeholder="Email"
                                    />

                                    <AddInputBox
                                        // className="addBoard__title"
                                        label="Password"
                                        name="password"
                                        type="password"
                                        placeholder="Password"
                                    />
                                </div>

                                <AddSubmitButton
                                    className="actionBtn"
                                    title="ZALOGUJ"
                                />
                            </>
                        )}
                    </Formik>
                )}
            </div>
        </div>
    );
};

export default Login;
