import * as catBase from "../api/categories";
import { toast } from "react-toastify";

const getCategories = async (setData) => {
    const categories = await catBase.get();
    if (!Array.isArray(categories.data)) throw toast.error("Error occured!");
    return setData([{ _id: "0", name: "wszystkie" }, ...categories.data]);
};

export { getCategories };
