import React from "react";
const defaultMessage = "";

const ErrorMessage = ({ visible = false, error = defaultMessage }) => {
    if (!visible) return null;
    return (
        <>
            <span className="error">{error}</span>{" "}
        </>
    );
};

export default ErrorMessage;
