import React, { useState, useEffect } from "react";

import ImageSlider from "../imageSlider";

import { BiImageAdd } from "react-icons/bi";

const ImageInput = ({ handleChange, img }) => {
    const [preview, setPreview] = useState(img);

    const imageArr = (arr) => {
        const result = [];

        for (let key in arr) {
            if (arr[key]["lastModified"]) {
                result.push(arr[key]);
            }
        }

        handleChange(result);
    };

    return (
        <div className="imageInput">
            <div className="imageInput__images">
                {preview &&
                    preview.map((img) => (
                        <img src={URL.createObjectURL(img)}></img>
                    ))}
            </div>

            <div className="imageInput__label">
                <label
                    htmlFor="asd"
                    onClick={() =>
                        document
                            .getElementById("asd")
                            .setAttribute("checked", true)
                    }
                >
                    <BiImageAdd />
                </label>
            </div>

            <input
                className="imageInput--input"
                checked={false}
                id="asd"
                type="file"
                name="images"
                multiple
                onChange={(e) => imageArr(e.target.files)}
            />
        </div>
    );
};

export default ImageInput;
