import React, { useEffect, useState } from "react";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import ImageSlider from "../common/imageSlider";
import SearchBox from "../common/searchBox";
import AcceptModal from "../common/modal";

import { dataFilter } from "../../../methods/filter";
import * as base from "../../../methods/data";
import * as time from "../../../methods/time";
import * as Forms from "../common/form/forms";
import Spinner from "../common/spinner";

const BoardCards = (props) => {
    const [data, setData] = useState(null);
    const [category, setCategory] = useState("0");
    const [text, setText] = useState("");
    const [modal, setModal] = useState(false);
    const [error, setError] = useState(null);
    const [load, setLoad] = useState(true);

    const { user, renderImage, title, history } = props;

    useEffect(() => {
        title(
            `${config.headers.board} ${
                category === "0" ? "" : "| " + category.toLowerCase()
            } ${text === "" ? "" : " ~ " + text.toLowerCase()}`
        );
    }, [category, text]);

    useEffect(async () => {
        try {
            await base.getNotices(setData);
        } catch (ex) {
            setError("Cant load data, plese try again");
        }
        setLoad(false);
    }, []);

    function expand(id) {
        document.getElementById(id).classList.toggle("show");
    }

    const handleDelete = async (card) => {
        setLoad(true);
        try {
            await base.noticeDelete(card);
            document.getElementById(card._id).remove();
            setModal(false);
        } catch (er) {}

        setLoad(false);
    };

    return (
        <div className="screen">
            <ToastContainer />

            {load && <Spinner />}

            {error && <Forms.Error error={error} visible={error} />}

            <div>
                <SearchBox
                    filtr={false}
                    handleSearch={setText}
                    status={{ category: category, text: text }}
                />

                {dataFilter(data, category, text).map((card) => (
                    <div
                        id={card._id}
                        className="board"
                        onClick={() => expand(card._id)}
                    >
                        <div className="board__header">
                            <div className="board__header--title">
                                <p>{card.title}</p>
                            </div>
                            <div className="board__header--details">
                                <p>
                                    {card.author
                                        ? card.author.login
                                        : "Undefined"}
                                </p>
                                <p>{time.getDate(card.date)}</p>
                            </div>
                        </div>

                        <div className="board__content">
                            <div
                                className="board__content--images"
                                onClick={(e) => e.stopPropagation()}
                            >
                                {card.text && <p>{card.text}</p>}
                                {card.images && (
                                    <ImageSlider
                                        images={card.images}
                                        handleClick={(i, index, arr) =>
                                            renderImage(arr, index)
                                        }
                                    />
                                )}
                            </div>
                            {card.author &&
                                user &&
                                user._id === card.author._id &&
                                !modal && (
                                    <div
                                        className="board__delete"
                                        onClick={(e) => e.stopPropagation()}
                                    >
                                        <button
                                            className="actionBtn edit"
                                            onClick={() =>
                                                history.push(
                                                    `/board/edit/${card._id}`
                                                )
                                            }
                                        >
                                            EDYTUJ
                                        </button>
                                        <button
                                            className="actionBtn delete"
                                            onClick={() => setModal(card)}
                                        >
                                            USUŃ
                                        </button>
                                    </div>
                                )}
                        </div>
                    </div>
                ))}
            </div>
            {modal && (
                <div onClick={(e) => e.stopPropagation()}>
                    <AcceptModal
                        close={setModal}
                        accept={() => handleDelete(modal)}
                        title={`Potwierdź operację usunięcia ${modal.title}`}
                    />
                </div>
            )}
        </div>
    );
};

export default BoardCards;
