import { toast } from "react-toastify";
import * as imageBase from "../api/images";

import * as noticeBase from "../api/notices";
import * as productBase from "../api/products";

const noticeDelete = async (card) => {
    try {
        card.images.forEach((img) => imageBase.remove(img));
        const result = await noticeBase.remove(card._id);
        if (result.ok) return result.data;
        else
            throw toast.error(
                "Error when delete. " + result.originalError.message
            );
    } catch (ex) {
        if (ex.response && ex.response.status === 404)
            throw toast.error("This product has already been deleted.");
        else throw toast.error("Error when delete.");
    }
};

export { noticeDelete };
