import React, { useState, useEffect } from "react";

import ImageSlider from "../imageSlider";

import { BiImageAdd } from "react-icons/bi";

const ImageInput = ({ handleChange, preview }) => {
    const imageArr = (arr) => {
        const result = [];

        for (let key in arr) {
            if (arr[key]["lastModified"]) {
                result.push(arr[key]);
            }
        }

        handleChange(result);
    };

    return (
        <>
            {preview &&
                preview.map((img) => (
                    <img src={URL.createObjectURL(img)}></img>
                ))}

            <label
                htmlFor="asd"
                onClick={() =>
                    document.getElementById("asd").setAttribute("checked", true)
                }
            >
                <BiImageAdd />
            </label>

            <input
                checked={false}
                id="asd"
                className="imageInput"
                type="file"
                name="images"
                multiple
                onChange={(e) => imageArr(e.target.files)}
            />
        </>
    );
};

export default ImageInput;
