import { apiClient as client } from "./client";

import img from "../../public/images/bg.jpg";

const endpoint = "/images";

const add = async (body) => {
    // body = Uint8ArrayFromBase64(body);
    const form = new FormData();

    var img = new Image(); // Create new img element
    img.src = body;

    form.append("file", img);

    client.setHeader("Content-Type", "multipart/form-data");
    client.post(endpoint, form);
};

function Uint8ArrayFromBase64(base64Url) {
    return Uint8Array.from(
        window.atob(base64Url.replace(/-/g, "+").replace(/_/g, "/")),
        (v) => v.charCodeAt(0)
    );
}

export { add };
