import React, { useEffect, useState } from "react";

import * as noticeBase from "../../api/notices";

import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import { Formik } from "formik";
import * as Yup from "yup";

import * as imagesBase from "../../api/images";

import AddTextBox from "./common/form/addTextBox";
import AddInputBox from "./common/form/addInputBox.jsx";
import AddSubmitButton from "./common/form/submitButton.jsx";
import ImageInput from "./common/form/imageInput";

const validationSchema = Yup.object().shape({
    title: Yup.string().required().min(3).max(120),
    text: Yup.string().max(6000),
});

const AddBoard = (props) => {
    const [image, setImage] = useState([]);
    const [edit, setEdit] = useState(null);

    useEffect(() => {
        props.title("ogłoszenie");
        checkEdit();
    }, []);

    const checkEdit = async () => {
        if (props.match.params.id) {
            const response = await noticeBase.notice(props.match.params.id);
            if (!response.ok) return;
            setEdit(response.data);
        }
    };

    const handleSave = async (item, image) => {
        if (image.length > 0 && typeof image[0] !== "string") {
            if (edit) {
                edit.images.forEach((img) => imagesBase.remove(img));
            }

            const images = await uploadImage(image);
            if (images.ok) item.images = images.data;
            else return toast.error("Error occured on saving! first");
        }

        if (edit) {
            item._id = edit._id;
            const response = await noticeBase.edit(item);
            response.ok
                ? props.history.push(`/board`)
                : toast.error("Error occured on saving! second");

            return;
        }

        const response = await noticeBase.add(item);

        response.ok
            ? props.history.push(`/board`)
            : toast.error("Error occured on saving! third");
    };

    const uploadImage = (image) => {
        const form = new FormData();
        image.forEach((img) => form.append("image", img));

        return imagesBase.add(form);
    };

    return (
        <div className="screen">
            <div className="screen__container">
                <div className="addBoard">
                    <div className="addBoard__images">
                        <ImageInput handleChange={setImage} preview={image} />
                    </div>

                    <Formik
                        enableReinitialize={true}
                        initialValues={
                            edit
                                ? {
                                      text: edit.text,
                                      title: edit.title,
                                  }
                                : {
                                      text: "",
                                      title: "",
                                  }
                        }
                        onSubmit={(values) => handleSave(values, image)}
                        validationSchema={validationSchema}
                    >
                        {({ handleSubmit }) => (
                            <>
                                <AddInputBox
                                    className="addBoard__title"
                                    name="title"
                                    placeholder="Nazwa"
                                    defaultValue={edit ? edit.title : null}
                                />
                                <AddTextBox
                                    className="addBoard__text"
                                    name="text"
                                    placeholder="Opis..."
                                    defaultValue={edit ? edit.text : null}
                                />
                                <div className="addBoard__buttons">
                                    <AddSubmitButton
                                        className="actionBtn"
                                        title={edit ? "ZAPISZ" : "OPUBLIKUJ"}
                                    />
                                </div>
                            </>
                        )}
                    </Formik>
                </div>
            </div>
        </div>
    );
};

export default AddBoard;
