import React, { useEffect, useState } from "react";

import * as noticeBase from "../../api/notices";
import ImageSlider from "./common/imageSlider";
import SearchBox from "./common/searchBox";

import * as auth from "../../api/auth";

import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import { dataFilter } from "../../methods/filter";
import AcceptModal from "./common/modal";

const BoardCards = (props) => {
    const [data, setData] = useState(null);
    const [category, setCategory] = useState("0");
    const [text, setText] = useState("");
    const [modal, setModal] = useState(false);

    const user = auth.getUser();

    useEffect(() => {
        props.title(
            `tablica ${category === "0" ? "" : "| " + category.toLowerCase()} ${
                text === "" ? "" : " ~ " + text.toLowerCase()
            }`
        );
    }, [category, text]);

    useEffect(() => {
        loadData();
    }, []);

    const loadData = async () => {
        const notices = await noticeBase.get();
        if (!Array.isArray(notices.data)) return;
        setData(notices.data);
    };

    function expand(e) {
        while (!e.target.classList.contains("board"))
            e.target = e.target.parentElement;

        const arr = e.target.children;

        for (let i = 0; i < arr.length; i++) {
            arr[i].classList.toggle("show");
            if (i === arr.length - 1) arr[i].classList.toggle("end");
        }
    }

    const handleDelete = async (id) => {
        try {
            const result = await noticeBase.remove(id);
            if (result.ok)
                return setData(data.filter((notice) => notice._id !== id));
            toast.error("Error when delete. " + result.originalError.message);
        } catch (ex) {
            if (ex.response && ex.response.status === 404)
                toast.error("This product has already been deleted.");
            else toast.error("Error when delete.");
        }

        setModal(false);
    };

    return (
        <div className="screen">
            <ToastContainer />
            {data && (
                <div className="screen__container">
                    <SearchBox
                        filtr={false}
                        handleSearch={setText}
                        status={{ category: category, text: text }}
                    />

                    {dataFilter(data, category, text).map((card) => (
                        <div className="board">
                            <div
                                className="board__title"
                                onClick={(e) => expand(e)}
                            >
                                <span>{card.title}</span>
                            </div>

                            <div className="board__content">
                                <div>
                                    {card.image && (
                                        <ImageSlider
                                            images={card.image}
                                            handleClick={(i, index, arr) =>
                                                props.renderImage(arr, index)
                                            }
                                        />
                                    )}
                                </div>

                                {card.text && <p>{card.text}</p>}

                                {user._id === card.author._id && !modal && (
                                    <div className="board__delete">
                                        <button
                                            className="actionBtn edit"
                                            onClick={() =>
                                                props.history.push(
                                                    `/board/edit/${card._id}`
                                                )
                                            }
                                        >
                                            EDYTUJ
                                        </button>
                                        <button
                                            className="actionBtn delete"
                                            onClick={() => setModal(true)}
                                        >
                                            USUŃ
                                        </button>
                                    </div>
                                )}

                                {modal && (
                                    <AcceptModal
                                        close={setModal}
                                        accept={() => handleDelete(card._id)}
                                        title={`Potwierdź operację usunięcia ${card.title}`}
                                    />
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default BoardCards;
