import { validationSchema } from "../models/login";
import { RiUserAddLine } from "react-icons/ri";
import React, { useState } from "react";
import { pick } from "lodash";
import Spinner from "../../common/spinner";

import { NavLink } from "react-router-dom";
import { Formik } from "formik";

import AddInputBox from "../../common/form/addInputBox.jsx";
import AddSubmitButton from "../../common/form/submitButton.jsx";
import Buttons from "../../common/buttons.jsx";

const LoginForm = (props) => {
    const { user, title, location } = props;

    const [load, setLoad] = useState(false);

    const handleLogin = async (user) => {
        setLoad(true);
        user.email = user.email.trim().toLowerCase();
        user = pick(user, ["email", "password"]);
        const response = await auth.login(user);

        if (response.ok) {
            localStorage.setItem("token", response.headers["x-auth-token"]);
            const { state } = location;
            window.location = state ? state.from.pathname : "/login";
        } else setLoad(false);
    };

    return (
        <>
            {load && <Spinner />}

            <Formik
                initialValues={{
                    email: "",
                    password: "",
                }}
                onSubmit={(values) => handleLogin(values)}
                validationSchema={validationSchema}
            >
                {({}) => (
                    <>
                        <div className="login__buttons">
                            <NavLink to="/register">
                                <RiUserAddLine className="specialBtn" />
                            </NavLink>

                            <Buttons
                                buttons={[
                                    {
                                        name: <RiUserAddLine />,
                                        ref: "/register",
                                    },
                                ]}
                            />

                            <AddInputBox
                                // className="addBoard__title"
                                name="email"
                                label={config.actions.email}
                                placeholder="Email"
                            />

                            <AddInputBox
                                // className="addBoard__title"
                                label={config.actions.password}
                                name="password"
                                type="password"
                                placeholder="Password"
                            />
                        </div>

                        <AddSubmitButton
                            className="actionBtn"
                            title={config.actions.login}
                        />
                    </>
                )}
            </Formik>
        </>
    );
};

export default LoginForm;
