import React, { useEffect, useState } from "react";

import config from "../../config/config.json";

import * as auth from "../../../services/auth";
import * as base from "../../../methods/data";

import LoginForm from "./forms/loginForm";
import Post from "./../board/post";
import Card from "./../shop/card";
import ButtonBack from "./../common/form/buttonBack";
import { NavLink } from "react-router-dom";

const Login = (props) => {
    const { user, title, location, history, match } = props;
    const [posts, setPosts] = useState(null);
    const [sell, setSell] = useState(null);
    const [authorData, setAuthorData] = useState(null);

    function redirect() {
        window.location = location.state
            ? location.state.from.pathname
            : "/user";
    }

    useEffect(async () => {
        if (!match.params.id) return;

        if (match.params.id === "sell") {
            const result = await base.getProducts();
            setSell(result);
        } else {
            const result = await base.getNotices();
            setPosts(result);
        }

        if (match.params.id === "posts") setPosts();

        const data = posts ? posts : sell;
        const result = data.filter(
            (card) => card.author && card.author._id == user._id
        );
        setAuthorData(result);
    }, [match.params]);

    useEffect(() => {
        title(user ? config.headers.user : config.headers.login);
    }, []);

    return (
        <div className="screen">
            {authorData && (
                <>
                    {posts && <Post data={authorData} {...props} />}
                    {sell && <Card data={authorData} {...props} />}
                    <span
                        onClick={() => {
                            setPosts(null);
                            setSell(null);
                            setAuthorData(null);
                        }}
                    >
                        <ButtonBack />
                    </span>
                </>
            )}

            {!authorData && (
                <div className="login">
                    {(user && (
                        <>
                            <div className="login__info">
                                <div>{config.info.loggedAs}</div>
                                <div>{user.login}</div>
                            </div>

                            <div>
                                <NavLink
                                    to={{
                                        pathname: "/user/sell",
                                        return: location.pathname,
                                    }}
                                >
                                    <button className="actionBtn">
                                        {config.user.sell}
                                    </button>
                                </NavLink>

                                <button
                                    className="actionBtn"
                                    onClick={async () =>
                                        setPosts(await base.getNotices())
                                    }
                                >
                                    {config.user.posts}
                                </button>
                            </div>
                            <button
                                className="actionBtn"
                                onClick={() => auth.logout()}
                            >
                                {config.actions.logout}
                            </button>
                        </>
                    )) ||
                        (!user && <LoginForm redirect={redirect} />)}
                </div>
            )}
        </div>
    );
};

export default Login;
