const checkEdit = async (id, noticeBase, setEdit, setImage) => {
    if (id) {
        const response = await noticeBase.notice(id);
        if (!response.ok) return false;
        setEdit(response.data);
        setImage(response.data.images);
    }
    return true;
};

export { checkEdit };
