import { toast } from "react-toastify";
import * as imagesBase from "../api/images";

const handleSave = async (item, edit, image, base) => {
    if (image.length > 0 && typeof image[0] !== "string") {
        if (edit) {
            edit.images.forEach((img) => imagesBase.remove(img));
        }

        const response = await uploadImage(image);
        if (response.ok) item.images = response.data;
        else return toast.error("Error occured on saving!");
    }

    if (edit) {
        item._id = edit._id;
        const response = await base.edit(item);

        if (!response.ok) {
            toast.error("Error occured on saving!");
            return false;
        }
        return true;
    }

    const response = await base.add(item);

    if (!response.ok) {
        toast.error("Error occured on saving!");
        return false;
    }
    return true;
};

const uploadImage = (image) => {
    const form = new FormData();
    image.forEach((img) => form.append("image", img));

    return imagesBase.add(form);
};

export { handleSave };
