import React, { useState, useEffect } from "react";

const ImageInput = ({ handleChange }) => {
    return (
        <input
            type="file"
            id="file"
            name="images"
            multiple
            onChange={(e) => handleChange(e.target.files)}
        />
    );
};

export default ImageInput;
