import {} from "../api/categories";
import { noticeBase } from "../api/notices";
import { productBase } from "../api/products";

const getOne = async (id, base, setData, setImage) => {
    if (id) {
        const response = await base.getOne(id);
        if (!response.ok) return false;
        setData(response.data);
        if (setImage) setImage(response.data.images);
    }
    return true;
};

const getOneNotice = async (id, setData, setImage) => {
    return await getOne(id, noticeBase, setData, setImage);
};

const getOneProduct = async (id, setData, setImage) => {
    return await getOne(id, productBase, setData, setImage);
};

const getNotices = async (setData) => {
    const notices = await noticeBase.get();
    if (!Array.isArray(notices.data)) return;
    setData(notices.data);
};

const getProducts = async (setData) => {
    const products = await productBase.get();
    if (!Array.isArray(productBase.data)) return;
    setData(productBase.data);
};

const getCategories = async (setData) => {
    const categories = await catBase.get();
    if (!Array.isArray(categories.data)) return;
    setData([{ _id: "0", name: "wszystkie" }, ...categories.data]);
};

export { getOneNotice, getOneProduct, getNotices, getProducts, getCategories };
