const dataFilter = (filterData, category = 0, text = 0, user = null) => {
    console.log(filterData);

    if (!filterData) return [];

    if (user)
        filterData = filterData.filter(
            (card) => user === (card.author && card.author._id)
        );
    console.log(filterData);

    if (category) {
        filterData = filterData.filter(
            (card) => category === (card.category && card.category._id)
        );
        console.log("FILTER CATEGORY", filterData);
    }

    if (text)
        filterData = filterData.filter(
            (card) =>
                card.title.toLowerCase().includes(text.toLowerCase()) ||
                card.text.toLowerCase().includes(text.toLowerCase())
        );

    console.log(user, category, text);
    console.log(filterData);

    return filterData.reverse();
};

export { dataFilter };
