import React, { useEffect, useState } from "react";
import { pick } from "lodash";
import { Formik } from "formik";

import { validationSchema } from "./models/register";

import AddInputBox from "../common/form/addInputBox.jsx";
import AddSubmitButton from "../common/form/submitButton.jsx";

import * as userBase from "../../../services/users";
import Spinner from "../common/spinner";

const Register = (props) => {
    const { title } = props;
    const [load, setLoad] = useState(false);

    useEffect(() => {
        title("register");
    }, []);

    const handleRegister = async (user) => {
        setLoad(true);

        user.email = user.email.trim().toLowerCase();
        user.login = user.login.trim();

        user = pick(user, ["login", "email", "password"]);
        const response = await userBase.add(user);

        if (response.ok) {
            localStorage.setItem("token", response.headers["x-auth-token"]);
            window.location = "/login";
        } else setLoad(false);
    };

    return (
        <div className="screen">
            {load && <Spinner />}

            <Formik
                initialValues={{
                    email: "",
                    login: "",
                    password: "",
                    confirmPassword: "",
                }}
                onSubmit={(values) => handleRegister(values)}
                validationSchema={validationSchema}
            >
                {({}) => (
                    <>
                        <div className="login">
                            <div className="login__buttons">
                                <AddInputBox
                                    name="email"
                                    label="email"
                                    placeholder="Email..."
                                />

                                <AddInputBox
                                    label="login"
                                    name="login"
                                    placeholder="Login..."
                                />
                                <AddInputBox
                                    label="Password"
                                    name="password"
                                    type="password"
                                    placeholder="Password"
                                />
                                <AddInputBox
                                    label="Password"
                                    name="confirmPassword"
                                    type="password"
                                    placeholder="Confirm password..."
                                />
                            </div>

                            <AddSubmitButton
                                className="actionBtn"
                                title="REGISTER"
                            />
                        </div>
                    </>
                )}
            </Formik>
        </div>
    );
};

export default Register;
