import { toast } from "react-toastify";
import * as imageBase from "../api/images";
import * as noticeBase from "../api/notices";
import * as productBase from "../api/products";

const noticeDelete = async (card) => {
    return await dlt(card, noticeBase);
};

const productDelete = async (card) => {
    return dlt(card, productBase);
};

const dlt = async (card, base) => {
    try {
        card.images.forEach((img) => imagesBase.remove(img));
        const result = await base.remove(card._id);
        if (result.ok) return props.history.push("/bazar");
        toast.error("Error when delete. " + result.originalError.message);
    } catch (ex) {
        if (ex.response && ex.response.status === 404)
            toast.error("This product has already been deleted.");
        else toast.error("Error when delete.");
    }
};

export { noticeDelete, productDelete };
