import React from "react";

import { BsFilterLeft } from "react-icons/bs";
import { BiSearchAlt } from "react-icons/bi";
import { IoFilterCircleOutline } from "react-icons/io5";
import { AiOutlineFileSearch } from "react-icons/ai";

const SearchBox = ({
    categories = null,
    handleSearch = null,
    handleCategorySearch = null,
    status,
    search = true,
}) => {
    const searchOpen = () => {
        document
            .getElementById("searchBox")
            .classList.toggle("searchBox--show");
    };

    return (
        <>
            {search &&
                ((status.text == 0 && (
                    <BiSearchAlt
                        className="icon icon--search"
                        onClick={searchOpen}
                    />
                )) || (
                    <AiOutlineFileSearch
                        className="icon icon--search"
                        onClick={searchOpen}
                    />
                ))}

            <div className="searchBox" id="searchBox">
                {categories && (
                    <>
                        <label for="searchBoxSelect">Select category </label>

                        <select
                            className="searchBox--window"
                            id="categoryBox"
                            onChange={(e) =>
                                handleCategorySearch(e.currentTarget.value)
                            }
                            defaultValue={"0"}
                            onSelect={() =>
                                document.getElementById("searchBox").focus()
                            }
                        >
                            {categories &&
                                categories.map((cat) => (
                                    <option value={cat._id}>{cat.name}</option>
                                ))}
                        </select>
                    </>
                )}

                {search && (
                    <>
                        <label for="searchBoxText">Search text</label>

                        <input
                            id="searchBoxText"
                            className=" searchBox--window"
                            onChange={(e) =>
                                handleSearch(e.currentTarget.value)
                            }
                        ></input>
                    </>
                )}
            </div>
        </>
    );
};

export default SearchBox;
