import React, { useEffect, useState } from "react";

import config from "../../config/config.json";

import * as auth from "../../../services/auth";
import * as base from "../../../methods/data";

import LoginForm from "./forms/loginForm";
import { NavLink } from "react-router-dom";

const Login = (props) => {
    const { user, title, location } = props;
    const [data, setData] = useState(null);

    function redirect() {
        window.location = location.state
            ? location.state.from.pathname
            : "/login";
    }

    useEffect(() => {
        title(user ? config.headers.user : config.headers.login);
    }, []);

    return (
        <div className="screen">
            <div className="login">
                {data &&
                    data
                        .filter(
                            (card) => card.author && card.author._id == user._id
                        )
                        .map((card) => <div>{card.title}</div>)}
                {user && (
                    <>
                        <div className="login__info">
                            <div>{config.info.loggedAs}</div>
                            <div>{user.login}</div>
                        </div>

                        <div>
                            <NavLink>
                                <button
                                    className="actionBtn"
                                    onClick={async () =>
                                        setData(await base.getProducts())
                                    }
                                >
                                    {config.user.sell}
                                </button>
                            </NavLink>

                            <button
                                className="actionBtn"
                                onClick={async () =>
                                    setData(await base.getNotices())
                                }
                            >
                                {config.user.posts}
                            </button>
                        </div>

                        <button
                            className="actionBtn"
                            onClick={() => auth.logout()}
                        >
                            {config.actions.logout}
                        </button>
                    </>
                )}

                {!user && <LoginForm redirect={redirect} />}
            </div>
        </div>
    );
};

export default Login;
