const dataFilter = (filterData, category = "0", text = "", user = null) => {
    if (!filterData) return [];

    console.log(user, category, text);

    if (user) filterData = filterData.filter((u) => user === u.author._id);

    const result = filterData.filter(
        (card) =>
            // user
            //     ? user == (card.author && card.author._id)
            //     : true &&
            (category === "0" || card.category._id === category) &&
            (text === "" ||
                card.title.toLowerCase().includes(text.toLowerCase()) ||
                card.text.toLowerCase().includes(text.toLowerCase()))
    );
    console.log(result);
    return result.reverse();
};

export { dataFilter };
