import React, { useEffect } from "react";

import { NavLink, Redirect } from "react-router-dom";

import { RiUserAddLine } from "react-icons/ri";

import { Formik } from "formik";
import * as Yup from "yup";

import * as authBase from "../../api/auth";

import AddInputBox from "./common/form/addInputBox.jsx";
import AddSubmitButton from "./common/form/submitButton.jsx";
import Buttons from "./common/buttons.jsx";

const validationSchema = Yup.object().shape({
    login: Yup.string()
        .trim()
        .required("Podaj login.")
        .min(5)
        .max(120)
        .label("Login"),
    password: Yup.string().min(5).max(6000),
});

const Login = (props) => {
    useEffect(() => {
        props.title("login");
    }, []);

    const handleLogin = async (user) => {
        user = pick(user, ["email", "password"]);
        const response = await authBase.login(user);
        localStorage.setItem("token", response.headers["x-auth-token"]);
        <Redirect to="/bazar" />;
    };

    return (
        <div className="screen">
            <Formik
                initialValues={{
                    login: "",
                    password: "",
                }}
                onSubmit={(values) => handleLogin(values)}
                validationSchema={validationSchema}
            >
                {({}) => (
                    <>
                        <div className="login">
                            <div className="login__buttons">
                                <NavLink to="/register">
                                    <RiUserAddLine className="specialBtn" />
                                </NavLink>

                                <Buttons
                                    buttons={[
                                        {
                                            name: <RiUserAddLine />,
                                            ref: "/register",
                                        },
                                    ]}
                                />

                                <AddInputBox
                                    // className="addBoard__title"
                                    name="login"
                                    label="Login"
                                    placeholder="Login"
                                />

                                <AddInputBox
                                    // className="addBoard__title"
                                    label="Password"
                                    name="password"
                                    type="password"
                                    placeholder="Password"
                                />
                            </div>

                            <AddSubmitButton
                                className="actionBtn"
                                title="LOGIN"
                            />
                        </div>
                    </>
                )}
            </Formik>
        </div>
    );
};

export default Login;
